var canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d"),width=window.innerWidth,height=window.innerHeight,dots=[],mousePos={x:-20,y:-20},colors=["#a8e6cf","#dcedc1","#ffd3b6","#ffaaa5","#ff8b94"];function Dot(t,e,o){var s=this;this.x=t,this.y=e,this.targetRadius=3,this.radius=3,this.color=o,this.draw=function(){var t=d2points(s,mousePos);s.targetRadius=t<100?3+(100-t)/7:3,s.radius=s.radius+.1*(s.targetRadius-s.radius),ctx.beginPath(),ctx.arc(s.x,s.y,s.radius,0,2*Math.PI,!1),ctx.fillStyle=s.color,ctx.fill()}}function placeDots(){dots=[];for(var t=0,e=14;e<width;e+=20)for(var o=14;o<height;o+=20){var s=new Dot(e,o,colors[t%colors.length]);dots.push(s),t++}}function drawDots(){for(var t=0;t<dots.length;t++)dots[t].draw()}function resize(){width=window.innerWidth,height=window.innerHeight,canvas.width=width,canvas.height=height,placeDots()}function addListeners(){canvas.addEventListener("mousemove",mousemove),canvas.addEventListener("touchstart",touchmove),canvas.addEventListener("touchmove",touchmove),window.addEventListener("resize",resize),window.addEventListener("touchmove",function(t){t.preventDefault()})}function mousemove(t){mousePos.x=t.pageX,mousePos.y=t.pageY}function touchmove(t){mousePos.y=t.targetTouches[0].pageY,mousePos.x=t.targetTouches[0].pageX}function loop(){ctx.clearRect(0,0,width,height),drawDots(),requestAnimationFrame(loop)}function d2points(t,e){var o=(o=e.x-t.x)*o,e=e.y-t.y;return e*=e,Math.sqrt(o+e)}resize(),addListeners(),loop();